/**
 * Global type declarations for LangSync Expo package
 */

/**
 * __DEV__ is a global variable that indicates whether the app is running in development mode.
 * This is automatically set by React Native / Expo bundler.
 * In production builds, __DEV__ is false and code within if (__DEV__) blocks is tree-shaken out.
 */
declare const __DEV__: boolean;
